      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(
        {
            sessionName: "session1", 
            number: number,
            text: msg
        }
    )
  });
  const content = await response.json();

  console.log(content);
})();  

